        //       <ins class="oj-tree-icon"> </ins>            <-- node icon
        //       <span class="oj-tree-title">Blogs</span>     <-- node title
        //       <ul role="group"> 
        //          <li . . .child nodes...
        //       </ul>
        //    </a>
